<style>
    #instagram{
        /*margin-top: 60px;*/
        min-height: 300px;
        overflow: hidden;
        background-color: var(--app-cool-white);
        /*background: #f2f2f2;*/
        background: #fff;
    }

    #instagram #content{
        position: relative;
        min-height: calc(100vh + 10px);
        background-color: var(--app-cool-white);
    }

    #instagram #text{
        padding: 6em 14em;
    }

    #instagram #text h2{
        font-size: 3em; font-family: 'Gotham ultra', sans-serif; margin-bottom: 0.4em;
    }

    #instagram #text p{
        font-family:'Gotham light', sans-serif;font-size: 1.6em; line-height: 1.5em;
    }

    #instagram #instaWall{
        -webkit-background-size: cover;
        background-size: 100%;
        background-position: center;
        background-image: url({{asset('images/insta-wall-2.jpg')}});
    }
</style>

